#include <string>

enum TokenType {
	// Separator/punctuator
	LEFT_PAREN, RIGHT_PAREN,
	LEFT_BRACE, RIGHT_BRACE,
	COLON,
	SEMICOLON,

	// Operators
	PLUS, MINUS, STAR, SLASH,
	AND, OR,

	BANG, BANG_EQUAL,
	EQUAL,
	GREATER, GREATER_EQUAL,
	LESS, LESS_EQUAL,

	// literals
	IDENTIFIER, NUMBER,

	// keywords
	IF, ELSE,
	DEF,
	PRINT, CHAR_PRINT, PEEK,
	DUP,
	SWAP, N_SWAP,

	// comments
	HASHTAG,

	// for parser
	END
};

// a data class for tokens generated by the scanner.
struct Token {
	TokenType type;
	std::string lexemme;
	int literal;
	int line;
};
